mapper.insert:
insert into file_trash(file_id, origin)
select data_id, parent_id from v_user_drive
where data_type = 1
and user_id = #{userId}
and data_id in

    #{fileId}

mapper.update:
update file_data f join file_trash t on f.file_id = t.file_id
set f.folder_id = t.origin
where user_id = #{userId}
and f.file_id in

    #{fileId}

mapper.select:
select count(1) from file_data f
left join file_trash t on f.folder_id = t.origin
where f.user_id = #{userId}
and folder_id != 999
and f.file_id = #{fileId}
and f.file_name = (select file_name from file_data where file_id = #{fileId})
