mapper.insert:
insert into folder_trash(folder_id, origin)
select data_id, parent_id from v_user_drive
where data_type = 0
and user_id = #{userId}
and data_id in

    #{folderId}

mapper.update:
update folder f join folder_trash t on f.folder_id = t.folder_id
set f.parent_id = t.origin
where user_id = #{userId}
and f.folder_id in

    #{folderId}

mapper.select:
select count(1) from folder f
left join folder_trash t on f.parent_id = t.origin
where f.user_id = #{userId}
and f.folder_id != 999
and f.folder_id = #{folderId}
and f.folder_name = (select folder_name from folder where folder_id = #{folderId})
